import React from 'react'

const SuggestedPost = ({ className, header, posts = [],tags }) => {
  return (
    <div className={`w-full shadow-[0_8px_30px_rgb(0,0,0,0.12) rounded-lg p-4 ${className}`}>
        <h2 className='font-roboto font-medium text-dark-hard'>{header}</h2>
        <div className='grid gap-y-5 mt-5'>
            {posts.map((item) => (
                <div 
                    key={item._id} 
                    className='flex space-x-3 flex-nowrap items-center'>
                    <img 
                        className='aspect-square object-cover rounded-lg w-1/5'
                        src={item.image} 
                        alt="laptop" 
                    />
                    <div className='text-sm font-roboto text-dark-hard font-medium'>
                        <h3>{item.title}</h3>
                        <span className='text-xs opacity-60'>
                            {new Date(item.createdAt).toLocaleDateString("en-US", {
                                day: 'numeric',
                                month: 'short',
                                year: 'numeric'
                            })}
                        </span>
                    </div>
                </div>
            ))}
        </div>
        <h2 className='flex flex-wrap gap-x-2 gap-y-2 mt-4'>Tags</h2>
        <div>
            {tags.map((item) => (
                <Link to='/'></Link>
            ))}
        </div>
    </div>
  )
}

export default SuggestedPost
